import Header from './components/Header';
import Banner from './components/Banner';
import SortSearchBar from './components/SortSearchBar';
import CardContainer from './components/CardContainer';
import CardViewer from './components/CardViewer';

import { useState, useEffect } from 'react';

function App() {
  const [ recipes, setRecipes ] = useState([]);
  const [ featuredRecipe, setFeaturedRecipe ] = useState({
    name: "",
    picture_url: "",
    cuisine: {
      name: "American"
    },
    ingredients: [
      {"name": "2x tomatos"},
      {"name": "2lbs beef"},
      {"name": "2x flour torillas"},
      {"name": "1x onion"}
    ],
    "directions": [
      {"text": "Take stuff out."},
      {"text": "Put stuff on stove."},
      {"text": "Enjoy good food."}
    ]
  })

  useEffect(() => {
    fetch(`http://localhost:4000/recipes`)
    .then(resp => resp.json())
    .then(data => {
      console.log(data)
      setRecipes(data)
      setFeaturedRecipe(() => recipes[Math.floor(Math.random() * recipes.length)])
    })
  }, [])

  console.log('Recipe', recipes)
  console.log('Featured recipe', featuredRecipe)

  return (
    <div>
      <Header />
      <Banner featuredRecipe = {featuredRecipe} />
      <SortSearchBar />
      <CardContainer recipes = {recipes}/>
      <CardViewer />
    </div>
  );
}

export default App;
