import { useState } from 'react';

function Banner({ recipes }) {
    // const [ featuredRecipe, setFeaturedRecipe ] = useState({
    //     name: '',
    //     picture_url: '',
    //     cuisine: '',
    //     ingredients: [],
    //     directions: []
    // })

    // function reloadFeaturedRecipe() {
    //     setFeaturedRecipe(recipes[Math.floor(Math.random() * recipes.length)])
    // }
    // reloadFeaturedRecipe();

     function getFeaturedRecipe() {
        return recipes[Math.floor(Math.random() * recipes.length)]
    }
    const featuredRecipe = getFeaturedRecipe();

    return (
        <div id="banner-container">
            <div id="banner-content">
                <div id="banner-text">
                    <h2>Recipe of the Day</h2>
                    <div id="featured-details">
                        <div id="featured-ingredients">
                            <h6>Ingredients</h6>
                            <ul>
                                <li>Ingredients go here</li>
                                <li>Ingredients go here</li>
                                <li>Ingredients go here</li>
                                <li>Ingredients go here</li>
                            </ul>
                        </div>
                        <div id="featured-instructions">
                            <h6>Instructions</h6>
                            <ol>
                                <li>Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. </li>
                                <li>Auctor eu augue ut lectus arcu bibendum. Amet mattis vulputate enim nulla aliquet porttitor lacus. Ullamcorper malesuada proin libero nunc consequat interdum varius sit amet. At urna condimentum mattis pellentesque id nibh. </li>
                                <li>Pretium aenean pharetra magna ac placerat vestibulum lectus mauris. Egestas integer eget aliquet nibh praesent tristique. Aliquet bibendum enim facilisis gravida neque convallis a. Sem et tortor consequat id porta nibh. </li>
                                <li>Ut tellus elementum sagittis vitae et leo duis. Non quam lacus suspendisse faucibus interdum posuere. Sed adipiscing diam donec adipiscing. Libero justo laoreet sit amet cursus sit amet. A erat nam at lectus urna duis convallis convallis.</li>
                            </ol>
                        </div>
                        <div id="featured-ingredients">
                            <h6>Ingredients</h6>
                            <ul>
                                {featuredRecipe.ingredients.map(ingredient => <li key={ingredient.name}>{ingredient.name}</li>)}
                            </ul>
                        </div>
                        {/* <div id="featured-instructions">
                            <h6>Instructions</h6>
                            <ol>
                                {featuredRecipe.directions.map(direction => {
                                    return (<li key={direction.text}>{direction.text}</li>)
                                })}
                            </ol>
                        </div> */}
                    </div>
                </div>
                <div id="banner-img-container">
                    <img src="https://www.foodiecrush.com/wp-content/uploads/2020/06/Chicken-Fajitas-foodiecrush.com-008.jpg" alt="Receipe of the Day" title="Recipe of the Day" />
                </div>

            </div>
        </div>
    )
}

export default Banner
