function Banner({ recipes }) {

    const featuredRecipe = recipes[Math.floor(Math.random() * recipes.length)]
    console.log(featuredRecipe)

    return (
        <div id="banner-container">
            <div id="banner-content">
                <div id="banner-text">
                    <h2>Recipe of the Day</h2>
                    <h6>Ingredients</h6>
                    <ul>
                        {featuredRecipe.ingredients.map(ingredient => {
                        return (<li>{ingredient.name}</li>)
                    })}
                    </ul>
                    <h6>Instructions</h6>
                    <ol>
                        {featuredRecipe.directions.map(direction => {
                        return (<li>{direction}</li>)
                    })}
                    </ol>

                </div>
                <div id="banner-img-container">
                    <img src="" alt="Receipe of the Day" title="Recipe of the Day" />
                </div>

            </div>
        </div>
    )
}

export default Banner
